// this line should not be removed
var wsgWatsonTranslate = angular.module( "wOliv2",[]);

wsgWatsonTranslate.controller( "wOliv2Controller", function( $scope ) {

    //Define Countries to be used in Countries combo GC
    $scope.code1 = null;
    $scope.code2 = null;
    $scope.inText = "";
    $scope.outText = "";
    $scope.FromTo = true;

    $scope.languages = [
        {lCode:"ptbr", lName:"Portuguese"},
        {lCode:"enus", lName:"English/US"},
        {lCode:"eses", lName:"Spanish/Spain"},
        {lCode:"frfr", lName:"French"},
        {lCode:"arar", lName:"Arabic"}
        ];

    $scope.translateIt = function() {
        var sid;
        var inputText;

        if ($scope.FromTo) {
            sid = 'mt-' + $scope.code1 + '-' + $scope.code2;
            inputText = $scope.inText;
        } else {
            sid = 'mt-' + $scope.code2 + '-' + $scope.code1;
            inputText = $scope.outText;
        }

        var dq = new DataQuery( 'qCallWatsonTranslationService' );
        dq.setParameters({"sid":sid, "txt":inputText});

        dq.execute().
        	done( function() {
        		$scope.$apply(function() {
        			$scope.results = dq.getData();
        			if ($scope.FromTo) {
                        $scope.outText = $scope.results.d[1].p[0].s[0].t[0]["$"];
                    }
                    else {
                        $scope.inText = $scope.results.d[1].p[0].s[0].t[0]["$"];
                    }
        		});
        	});

        return '';

    };

    $scope.changeFromTo = function(arg) {
        $scope.FromTo = !($scope.FromTo);
        return '';
    };

	$scope.checkIt = function() {
	   if ($scope.code1 == null || $scope.code2 == null) {
	        return true;
	   }
	   else {
	        return false;
	   }
	};

});
