// this line should not be removed
var wOpenDialog = angular.module("wOpenDialog",[]);
console.log("after wOpenDialog module");

wOpenDialog.controller( "wOpenDialogController", function( $scope, $compile ) {
    console.log("within wOpenDialogController");
    //var dialogCallback = function(event, btn) {
    var dialogCallback = function(btn) {
        //console.log(event.data);
        console.log(btn);
    };

	$scope.openHtmlDialog = function(arg) {
	    dfxOpenDialog({
	        'title': 'My Html Content',
	        'top': '100px',
	        'left': '25%',
	        'width': '400px',
	        'height': '200px',
	        'html': '<p>Open dialog HTML content, paragraph 1</p><p>Open dialog HTML content, paragraph 2</p>',
	        'buttons': 'OK_CANCEL'
	    });
	};
	$scope.openFormWidgetDialog = function(arg) {
	    dfxOpenDialog({
	        'title': 'My Dialog Form',
	        'width': '50%',
	        'height': '50%',
	        'widgetName': 'wForm',
	        'buttons': 'YES_NO_CANCEL',
	        'callback': dialogCallback
	    }, $scope, $compile);
	};
	$scope.openChartWidgetDialog = function(arg) {
	    dfxOpenDialog({
	        'title': 'My Chart',
	        'headerColor': 'orange',
	        'width': '600px',
	        'height': '350px',
	        'buttons': 'OK',
	        'widgetName':'wChart'
	    }, $scope, $compile);
	};
});
