// this line should not be removed
var wPanelDynamic = angular.module("wPanelDynamic",[]);

wPanelDynamic.controller( "wPanelDynamicController", function( $scope ) {
    $scope.panels = {};
    var dq = new DataQuery( 'qPeopleFiltered' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.panels = dq.getData();
    		});
    	});
    $scope.myFunction = function(arg) {
        console.log($scope.panels[arg]);
    }
});

