var wPanelTile = angular.module("wPanelTile",[]);

wPanelTile.controller( "wPanelTileController", function( $scope ) {

	    /*$scope.customerOrders = [
                {"orderId": 1003, "productId": 10, "productImage": "image10.jpg", "quantity": 20, "unitPrice": 30},
                {"orderId": 1004, "productId": 15, "productImage": "image15.jpg", "quantity": 15, "unitPrice": 70}
                ];*/
    $scope.customerOrders = [];
    $scope.ordersShowHide = [];
    $scope.showOneDetailOnly = true;
    $scope.currentlyOpenedIndex = -1;

    $scope.customers = [
        {
            "id": 1,
            "companyName": "IBM",
            "companyAdress": "1 White Plains Rd, Armonk, NY 10056",
            "businessType": "Technology",
            "orders": [
                {"orderId": 1003, "productId": 10, "productImage": "image10.jpg", "quantity": 20, "unitPrice": 30},
                {"orderId": 1004, "productId": 15, "productImage": "image15.jpg", "quantity": 15, "unitPrice": 70}
                ],
            "ordersNbr": 2
        },
        {
            "id": 2,
            "companyName": "Google",
            "companyAdress": "1 Google Dr, Palo Alto, CA 94001",
            "businessType": "Technology",
            "orders": [
                {"orderId": 2001, "productId": 2, "productImage": "image2.jpg", "quantity": 50, "unitPrice": 50},
                {"orderId": 2002, "productId": 6, "productImage": "image6.jpg", "quantity": 10, "unitPrice": 20},
                {"orderId": 2004, "productId": 7, "productImage": "image7.jpg", "quantity": 15, "unitPrice": 90}
                ],
            "ordersNbr": 3
        },
        {
            "id": 3,
            "companyName": "Apple",
            "companyAdress": "1 Apple Way, Palo Alto, CA 94001",
            "businessType": "Technology",
            "orders": [
                {"orderId": 3001, "productId": 8, "productImage": "image8.jpg", "quantity": 30, "unitPrice": 75},
                {"orderId": 3002, "productId": 7, "productImage": "image7.jpg", "quantity": 40, "unitPrice": 90},
                {"orderId": 3003, "productId": 1, "productImage": "image1.jpg", "quantity": 70, "unitPrice": 100},
                {"orderId": 3004, "productId": 9, "productImage": "image9.jpg", "quantity": 10, "unitPrice": 50}
                ],
            "ordersNbr": 4
        }
    ];


    $scope.showHide = function(selectedIndex) {

        //Close currently Open index if there is one that is open
	    if ($scope.showOneDetailOnly && ($scope.currentlyOpenedIndex >=0) && ($scope.currentlyOpenedIndex != selectedIndex)) {
	            $scope.ordersShowHide[$scope.currentlyOpenedIndex] = false;
	    }
	    //Open close the one of the index where we are clicking
        $scope.ordersShowHide[selectedIndex] = !$scope.ordersShowHide[selectedIndex];
	    $scope.currentlyOpenedIndex = selectedIndex;

	    return '';
	};

    $scope.cloneJSON = function (selectedIndex) {
        $scope.customerOrders = [];
        //$scope.customerOrders.push($scope.customers[selectedIndex].orders);
        for(var i=0;i<$scope.customers[selectedIndex].orders.length;i++) {
            //$scope.customerOrders.push($scope.customers[selectedIndex].orders);
            $scope.customerOrders[i].orderId.push($scope.customers[selectedIndex].orders.orderId);
            $scope.customerOrders[i].productId.push($scope.customers[selectedIndex].orders.productId);
            $scope.customerOrders[i].productImage.push($scope.customers[selectedIndex].orders.productImage);
            $scope.customerOrders[i].quantity.push($scope.customers[selectedIndex].orders.quantity);
            $scope.customerOrders[i].unitPrice.push($scope.customers[selectedIndex].orders.unitPrice);
            alert('customersOrders ' + i + ' = ' + $scope.customerOrders[i].orderId);
        }
    }

    $scope.showHideAndClone = function (selectedIndex) {
        $scope.showHide(selectedIndex);
        $scope.cloneJSON (selectedIndex);
        /*if ($scope.customerOrdersShowHide[selectedIndex] == true) {
            alert('coucou inside s/h/c');
            $scope.customerOrders = $scope.cloneJSON (selectedIndex);
        }*/
        return '';
    }











});
