var wRadio = angular.module("wRadio",[]);

wRadio.controller( "wRadioController", function( $scope, $timeout ) {
    // dynamic radio, values from hard coded array
	$scope.vehicle = [
        {
            "name": "I have a bike",
            "val": "Bike",
            "disabled": false
        },
        {
            "name": "I have a car",
            "val": "Car",
            "disabled": false
        }
    ];

    // dynamic radio, values from data query
    var dq = new DataQuery( 'qAnimals' );
    dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.animal = dq.getData();
		});
	});

    // initial values
    $scope.myVehicle = 'Bike';
    $scope.myAnimal =  'Dog';
    $scope.mySport =  'Hockey';

    // add data to dynamic radio
    $scope.addVehicle = function(val) {
        $scope.vehicle.push({
            "name": "I have a " + $scope.newVehicleName,
            "val": $scope.newVehicleValue,
            "disabled": false
        });

        // empty input fields after adding new vehicle
        $scope.newVehicleName = '';
        $scope.newVehicleValue = '';
    }

    // change disable attribute of dynamic radio
    $scope.disableEnableAnimal = function(val) {
        /*var el = $('[name="animal_name"]');
