// this line should not be removed
var wSRTest = angular.module("wSRTest",[]);

wSRTest.controller( "wSRTestController", function( $scope ) {

	var dq = new DataQuery( 'qSRTest' );
	dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().data;
		});
	});



});
