// this line should not be removed
var wSampleGrid = angular.module("wSampleGrid",[]);

wSampleGrid.controller('wSampleGridController', ['$scope', '$sce', function($scope, $sce) {
	$scope.name = "John Smith";
	$scope.test_html = $sce.trustAsHtml("<b>Hello {{name}}</b>");
	
	
	$scope.results = [
        { "name": "<i>ACME Corp.</i>",
         "city": "New York",
         "chartdata": "1:2,2:4,3:1,4:3,4:2,6:4,7:1,8:3,9:2,10:4,11:1,12:3"
        },
        { "name": "Dupont et Cie",
         "city": "Paris",
         "chartdata": "1:4,2:3,3:2,4:1,5:4,6:3,7:2,8:1,9:4,10:3,11:2,12:1"
        },
        { "name": "McGuiness",
         "city": "London",
         "chartdata": "1:3,2:1,3:4,4:2,5:3,6:1,7:4,8:2,9:3,10:1,11:4,12:2"
        }
	];
	
	$scope.gridDataLoaded = function(data, dt) {
         $('span.bar').sparkline('html', { type: 'bar' });
    };
}]);
