// this line should not be removed
var wSelectCandidate = angular.module("wSelectCandidate",[]);

wSelectCandidate.controller( "wSelectCandidateController", function( $scope ) {
     var dq = new DataQuery( 'qBPMGetTaskDetails' );
	//dq.setParameters( {"task_id": sessionStorage.task_id} );
	dq.setParameters( {"task_id": "761"} );
    dq.execute().
        done( function() {
            $scope.$apply(function() {
                $scope.task_details = dq.getData().data.data.variables;
                $scope.cadidates=dq.getData().data.data.variables.ListOfCandidates.Candidate.items;

            });
        });

    $scope.selectCandidate = function(arg) {


        var dq_sel_candidate = new DataQuery( 'qBPMSelectCandidate' );
        dq_sel_candidate.setParameters(
            {   "task_id": sessionStorage.task_id,
                "action": "finish",
                "decision": "",
                "lastName": arg.rowdata.lastName,
                "firstName": arg.rowdata.firstName,
                "supervisor": arg.rowdata.supervisor,
                "payLevel": arg.rowdata.payLevel,
                "payType": arg.rowdata.payType,
                "hrcandidateAvailable": true
            }
        );
        dq_sel_candidate.execute().
            done( function() {
                window.location = "Home.html";
            });

    };


});

