src_script:
// this line should not be removed
var wTwitter = angular.module( "wSideTwitter",[]);

wTwitter.controller( "wSideTwitterController", function( $scope ) {
     var dq = new DataQuery( 'qReadTweets' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData();
		});
	});

});

