// this line should not be removed
var wStatistics = angular.module("wStatistics",[]);

wStatistics.controller( "wStatisticsController", function( $scope ) {

    var dq = new DataQuery( 'qPopulationTrends' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData();
		});
	});

	$scope.myFunction = function(arg) {

	    var dq = new DataQuery( 'qStatistics' );
	    dq.setParameters({"country":$scope.country});
dq.execute().
	done( function() {
		 $scope.$apply(function() {
                $scope.data = dq.getData();
                console.log($scope.data.series,$scope.data.data);
            })
	});

	};


});
