src_script:
// this line should not be removed
var wTabsDynamicClasses = angular.module("wTabsDynamicClasses",[]);

wTabsDynamicClasses.controller( "wTabsDynamicClassesController", function( $scope ) {
	var dq = new DataQuery( 'qPriorityTasks' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.panel_data = dq.getData();
		});
	});
	$scope.options=[{"value":"Postpone"},{"value":"Cancel"},{"value":"Start"}];
	$scope.showPriority = function(arg)  {
    $.SmartMessageBox({
        title : "Task Priority",
        content : "This is a <b>"+arg+"</b> Priority task",
        buttons : '[Ok]'
    });
};

});

