// this line should not be removed
var wTaskList = angular.module("wTaskList",[]);

wTaskList.controller( "wTaskListController", function( $scope ) {
    var dq = new DataQuery( 'qGetTasks' );
    dq.execute().
        done( function() {
            $scope.$apply(function() {
                $scope.tasks = dq.getData().data.data;
            });
        });

    $scope.openTask = function(arg) {
        if (arg.rowdata.taskSubject=='GM Approval Requisition') {
            sessionStorage.task_id = arg.rowdata.taskId;
            window.location = "approve_requisition.html";
        } else if (arg.rowdata.taskSubject==='Step: Interview') {
            sessionStorage.task_id = arg.rowdata.taskId;
            window.location = "interview.html";
        } else if (arg.rowdata.taskSubject==='Step: Hiring approval') {
            //sessionStorage.task_id = taskId;
            //window.location = "hire.html";
        }
         else if (arg.rowdata.taskSubject==='Step: Select candidate for interview') {
            sessionStorage.task_id = arg.rowdata.taskId;
            window.location = "selectcand.html";
        }
        else if (arg.rowdata.taskSubject==='Step: Interview') {
            sessionStorage.task_id = arg.rowdata.taskId;
            window.location = "interview.html";
        }

    };

});
