// this line should not be removed
var wTemp1 = angular.module("wTemp1",[]);

wTemp1.controller( "wTemp1Controller", function( $scope ) {

	var dq = new DataQuery( 'qBPMGetListCandidates' );
	dq.execute().
	done( function() {
		$scope.$apply(function() {
            	$scope.candidates = dq.getData().data;
            	$scope.candidateInfo = $scope.candidates[0];
		});
	});

    $scope.flipCarousel=function (dsds){
        //console.log(dsds.carouselIndex);
        $scope.$apply(function() {
        $scope.candidateInfo=$scope.candidates[dsds.carouselIndex];	});
    };




});
