var wTree = angular.module( "wTree",[]);

wTree.controller( "wTreeController", function( $scope ) {
    $scope.output="Click On the Tree Elemetns to Explore Tree!"
    $scope.my_tree = {}; 
    $scope.my_tree_handler = function(branch) { 
        var _ref; 
        $scope.output = "You selected: " + branch.label; 
        if ((_ref = branch.data) != null ? _ref.description : void 0) { 
            return $scope.output += '(' + branch.data.description + ')'; 
        } 
    }; 



    apple_selected = function(branch) { 
        return $scope.output =  branch.label + " " + branch.data; 
    }; 

    var treedata_avm = [ 
      { 
        label: 'MEAN', 
        children: [ 
          { 
            label: 'MongoDb', 
            data: { 
              description: "Non-SQL Database" 
            } 
          }, { 
            label: 'ExpressJS', 
            data: { 
              description: "NodeJS Package for handling HTTP services" 
            } 
          }, { 
            label: 'AngularJS', 
            data: { 
              description: "Best FrontEnd JS Library" 
            } 
          }, { 
            label: 'NodeJS', 
            data: { 
              description: "Non-Blocking JS driven Non-Blocking server"
            } 
          } 
        ] 
      }, { 
        label: 'LAMP', 
        data: { 
          definition: "A plant or part of a plant used as food, typically as accompaniment to meat or fish, such as a cabbage, potato, carrot, or bean.", 
          data_can_contain_anything: true 
        }, 
        children: [ 
          { 
            label: 'Linux' 
          },
          { 
            label: 'Apache' 
          },
          { 
            label: 'MySQL' 
          },{ 
            label: 'P', 
            data : 'Stands for PHP-Perl-Python', 
            onSelect: apple_selected, 
            children: [ 
              { 
                label: 'PHP', 
                data:'PHP is a server-side scripting language designed for web development' 

                , 
                onSelect: apple_selected 
              }, { 
                label: 'Perl', 
                data: 'Perl is a family of high-level, general-purpose, interpreted, dynamic programming languages.', 
                onSelect: apple_selected
              }, { 
                label: 'Python', 
                onSelect: apple_selected,
                data: 'Python supports multiple programming paradigms, including object-oriented, imperative and functional programming or procedural styles.'
              } 
            ] 
          } 
        ] 
      }
    ]; 
    $scope.my_data = treedata_avm;
});
