// this line should not be removed
var wTwitterOP = angular.module("wTwitterOP",[]);

wTwitterOP.controller( 'wTwitterOPController', function( $scope ) {
    var dq = new DataQuery( 'qTwitterOPfiltered' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.list = dq.getData();
    		});
    	});
});
