// this line should not be removed
var wUsaPopulationStatistics = angular.module("wUsaPopulationStatistics",[]);

wUsaPopulationStatistics.controller( "wUsaPopulationStatisticsController", function( $scope ) {

    $scope.getPopulation = function(arg) { 
var dq = new DataQuery( 'qUsaPopulation' );
dq.setParameters({"trim_start":$scope.inputInitialValue,"trim_end":$scope.inputInitialValue1});
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData();
		});
	});
    };
    $scope.getPopulation();
   $scope.value='2011-12-31';
});
