src_script:
/*Using First Combobox onChange Function to Trigger a query that returns the content of the Second DQ*/

// this line should not be removed
var wUsaStatesCities = angular.module("wUsaStatesCities",[]);

wUsaStatesCities.controller( "wUsaStatesCitiesController", function( $scope ) {
    //Calling the First DataQuery that Fills the First Combobox content
    var dq = new DataQuery( 'qUsaStates' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.states = dq.getData();
    		});
	});

    //the onchange function of first Combobox calls second query and load content in the second combobox
	$scope.GetCities = function(arg) {
	    var dq = new DataQuery( 'qStateCity' );
	    dq.setParameters({"state":$scope.state});
        dq.execute().
        	done( function() {
        		$scope.$apply(function() {
        			$scope.cities = dq.getData();
        		});
	       });

	};

	//the onchange function of second Combobox calls third query and load content in the datagrid
	$scope.GetDetails = function(arg) {
	    var dq = new DataQuery( 'qCityDetails' );
	    dq.setParameters({"state":$scope.state,"city":$scope.city});
        dq.execute().
        	done( function() {
        		$scope.$apply(function() {
        			$scope.cityDetails = dq.getData();
        		});
        });

    };
});
