// this line should not be removed
var wGetNews = angular.module( "wUsaToday",[]);

wGetNews.controller( "wUsaTodayController", function( $scope ) {
    var dq = new DataQuery( 'qsgNyTimes' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.news = dq.getData().results;
    			console.log($scope.news);
    		});
    	});


	$scope.openArticle = function (selectedIndex) {

	    var newWindow = window.open(
	        $scope.news[selectedIndex].url, "_blank");
	   return '';
	};


});
