// this line should not be removed
var wsgWatsonTranslate = angular.module( "wWatsonTranslate2",[]);

wsgWatsonTranslate.controller( "wWatsonTranslate2Controller", function( $scope ) {

    //Define Countries to be used in Countries combo GC
    $scope.code1 = null;
    $scope.code2 = "enus";
    //$scope.text1 = "";
    $scope.text2 = "";

    //Define thelanguage codes accepted by Watson translation service
    $scope.languages = [
        {lCode:"ptbr", lName:"Portuguese"},
        {lCode:"enus", lName:"English/US"},
        {lCode:"eses", lName:"Spanish/Spain"},
        {lCode:"frfr", lName:"French"}
        ];

    $scope.errorMsgs = [
        {
            title: "Warning",
            content: "<i class='fa fa-flag'></i><b>  This language is not recognized or is unsupported</b></i>",
            color: "orangeRed",
            icon: "fa fa-shield fadeInRight animated",
            iconSmall: "fa fa-flag",
            timeout: 4000
        },
        {
            title: "Warning",
            content: "<i class='fa fa-flag'></i><b>  You can't translate from and to the same language. Choose another language</b></i>",
            color: "orangeRed",
            icon: "fa fa-shield fadeInRight animated",
            iconSmall: "fa fa-flag",
            timeout: 4000
        }
        ];


    //------------------ Call Watson Tranlsation service
    $scope.detectAndTranslate = function() {
        var sid;

        $scope.text2 = "";

        //Call the service to detect the language based on the text entered in text1
        var dq = new DataQuery( 'qWatsonLangId' );
        dq.setParameters({"sid":"lid-generic", "txt": $scope.text1});
    	dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.langSet = dq.getData();

    			 //console.log ($scope.results);
    			 //alert("language is " + $scope.results.lang);

    			 //adjust the right code from the first call to get it ready for the second call
    			 if ($scope.langSet.lang == "pt-BR") {
    			     $scope.code1 = "ptbr";
    			 } else if ($scope.langSet.lang == "fr-FR") {
    			     $scope.code1 = "frfr";
    			 } else if ($scope.langSet.lang == "en-US") {
    			     $scope.code1 = "enus";
    			 } else if ($scope.langSet.lang == "es-ES") {
    			     $scope.code1 = "eses";
    			 } else {
    			     $scope.fancyAlert(0);
    			     return '';
    			 }

    			 //If language is other than these 4 supported languages, display an error message and leave
    			 if ($scope.code1 == $scope.code2) {
    			     $scope.fancyAlert(1);
    			     return '';
    			 }

                //Translate the message using the second service
                sid = 'mt-' + $scope.code1 + '-' + $scope.code2;
                //alert (sid);
                var dq2 = new DataQuery( 'qWatsonTranslate' );
                dq2.setParameters({"sid":sid, "txt":$scope.text1});

                dq2.execute().
                	done( function() {
                		$scope.$apply(function() {
                			$scope.results = dq2.getData();
                            $scope.text2 = $scope.results.d[1].p[0].s[0].t[0]["$"];
                		});// end apply dq2
