// this line should not be removed
var wWeather = angular.module( "wWeather",[]);

wWeather.controller( "wWeatherController", function( $scope ) {


    //---------------------- This code is executed the first time the widget is created --------------------------
    $scope.showPanel = false;
    $scope.someLocation = "";
    //---------------------- end code   

    //------------------ Get the Weather forecast
    $scope.getWeatherForecast = function() {
        var dq = new DataQuery( 'qOpenWeatherAPI' );
        dq.setParameters({"q":$scope.someLocation});
    	dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.allWeather = dq.getData().weatherdata.forecast[0].time;
    			$scope.showPanel = true;
    		});
    	});
    };
    //---------------------- End function --------------------------


    //-------------- Change the index of the "card" to be shown" and apply it to the "model structure" used by the GC
    $scope.navigate = function(arg) {

        $scope.currentIndex = $scope.currentIndex + arg;

        if ($scope.currentIndex < 0) {
            $scope.currentIndex = $scope.myAvatars.length - 1;
        } else if ($scope.currentIndex >= $scope.myAvatars.length) {
            $scope.currentIndex = 0 ;
        }

        $scope.selAvatar = $scope.myAvatars[$scope.currentIndex]; 

        return '';
    }; 


});


//custom angular temerature filter
wWeather.filter('temp', function($filter) {
    return function(input, precision) {
        if (!precision) {
            precision = 1;
        }
        var numberFilter = $filter('number');
        return numberFilter(input, precision) + '\u00B0C';
    };
});

