// this line should not be removed
var wWeatherHorizon = angular.module("wWeatherHorizon",[]);

wWeatherHorizon.controller( "wWeatherHorizonController", function( $scope ) {
	var dq = new DataQuery( 'qOpenWeatherAPI' );
	dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().weatherdata.forecast[0].time;
		});
	});

});
//custom angular temerature filter
wWeatherHorizon.filter('temp', function($filter) {
    return function(input, precision) {
        if (!precision) {
            precision = 1;
        }
        var numberFilter = $filter('number');
        return numberFilter(input, precision) + '\u00B0C';
    };
});
