// this line should not be removed
var wcDemoPatients = angular.module( "wcDemoPatients",[]);

wcDemoPatients.controller( "wcDemoPatientsController", function( $scope ) {

	var dq = new DataQuery( 'qcDemoGetAllPatients' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.patients = dq.getData().data;
    		});
    	});


});
