// this line should not be removed
var wcTasklistChart = angular.module("wcTasklistChart",[]);

wcTasklistChart.controller( "wcTasklistChartController", function( $scope ) {
	var dq = new DataQuery( 'qGetTasksChart' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData();
		});
	});
});
