// this line should not be removed
var w1 = angular.module( "wsgAccordionDynamic",[]);

w1.controller( "wsgAccordionDynamicController", function( $scope ) {

    $scope.users = [
        {
            "id": 12,
            "firstName": "Samantha",
            "lastName": "White",
            "occupation": "writer"
        },
        {
            "id": 217,
            "firstName": "Svetlana",
            "lastName": "Igoreva",
            "occupation": "artist"
        },
            {
            "id": 445,
            "firstName": "Mika",
            "lastName": "Tawada",
            "occupation": "singer"
        }
    ];

    $scope.usersAccordion = [];



    $scope.createUsersForAccordion = function() {
        var resp = [];
        var index = 1;

        $scope.usersAccordion = $scope.users.forEach(function(item) {
            odd = index % 2;
            resp.push({
                'label' :'User #'+item.id,
                'disabled': false,
                'open': (index>1 ? false : true),
                'classes': (odd ? 'info-odd' : 'info-even'),
                'link': 'http://abc.com/data/users/' + item.id + '/',
                'content': {
                    'userName': item.firstName,
                    'userLastName': item.lastName,
                    'occupation': 'Professional occupation: ' + item.occupation,
                    'name': item.firstName + ' ' + item.lastName
                    }
                });
            index++;
        });

        $scope.usersAccordion = resp;
        return '';
    };


    $scope.createUsersForAccordion();

    $scope.clickMe = function(arg) { 
        alert(' you have just clicked on row ' + arg);
        return '';
    };

});
