// this line should not be removed
var w1 = angular.module( "wsgCallWatson",[]);

w1.controller( "wsgCallWatsonController", function( $scope ) {

	// Model properties
    //$scope.question = 'What are the best place to stay in New York?';
    $scope.notFirstTime = false;

    $scope.sendQuestion = function() {
        var dq = new DataQuery( 'qCallWatsonFiltered' );
        dq.setReqBody( {"question":{ "questionText": $scope.question }} );
        dq.execute().
            done( function() {
                $scope.$apply(function() {
                    console.log(dq.getData());
                    $scope.answers = dq.getData().evidencelist;
                    $scope.notFirstTime = true;
                });
            });

    };




});
