// this line should not be removed
var w1 = angular.module( "wsgCallWatsonEnhanced",[]);

w1.controller( "wsgCallWatsonEnhancedController", function( $scope ) {

	/*
	$(document).ready( function () {
        var el = $('[name=knKnob1]');
        el.knob({
            "min": 0,
            "max": 150,
            "step": 1,
            "width": "60px",
            "height": "60px"
        })
    });
	*/


	$scope.corpuses = [{'label': 'travel'}, {'label': 'healthcare'}, {'label': 'wikivoyage'}];
	$scope.selectedCorpus = 'travel';
	$scope.showAnswers = false;

    $scope.sendQuestion = function() {
        var dq = new DataQuery( 'qCallWatsonEnhanced' );
        dq.setReqBody( {"question":{ "questionText": $scope.question }} );
        dq.setParameters ({"corpus" : $scope.selectedCorpus});
        dq.execute().
            done( function() {
                $scope.$apply(function() {
                    //console.log(dq.getData());
                    $scope.answers = dq.getData();
                    $scope.showAnswers = true;
                });
            });
    };

	$scope.showMoreOrLess = function(arg) {
	    //to be used with the slider, if any... $scope.answers[arg].importance = ($scope.answers[arg].rating) <=2 ? false : true;
	    if ($scope.answers[arg].importance == true) {
	        $scope.answers[arg].importance = false;
	        $scope.answers[arg].moreOrLess = 'more details...';
	    } else{
	     	$scope.answers[arg].importance = true;
	        $scope.answers[arg].moreOrLess = 'summary';
	    }

	    return '';
	};


});
