src_script:
// this line should not be removed
var wG1 = angular.module( "wsgTwitterFeeds",[]);

wG1.controller( "wsgTwitterFeedsController", ['$scope', '$sce', function($scope, $sce) {

    $scope.showFeeds = false;
    //$scope.scrname = 'CIGNA';

    $scope.getTweets = function() { 
    var dq = new DataQuery( 'qTwitter' );
    dq.setParameters({"screen_name":$scope.scrname});
    dq.execute().
        done( function() {
        $scope.$apply(function() {
            $scope.results = dq.getData();
            });
        });

    $scope.showFeeds = true;
    };


    $scope.openWebsite = function (arg) {
	    var newWindow = window.open(
	        $scope.results[arg].user.url, "_blank");
	   return '';
	};



}]);

